import styled from 'styled-components'

export const Container = styled.div`

      background: #232129;
      border-radius: 10px;
      border: 2px solid #232129;
      padding: 16px;
      width: 100%;
      color: #FFFFFF;
      display: flex;
      align-items: center;
input{
      background: transparent;
      flex: 1;
      border: 0;
      &::placeholder{
        color: #636660;
      }

      & + input {
        margin-top: 8px;
      }


    }
    > svg {
         margin-right: 16px;
      }

`
