import styled from 'styled-components'

export const Container = styled.div`
  position: relative;

  span {
    background: #FF9000;
    padding: 8px;
    border-radius: 4px;
    font-size: 14px;
    font-weight: 500;
    opacity: 0;
    transition: opacity 0.4s;
  }
`
