import styled from 'styled-components'

export const Container = styled.div`
position: absolute;
right: 0;
top: 0;
padding: 30px;
overflow: hidden;
`

export const Toast = styled.div`
width: 360px;
position: relative;
padding: 16px 30px 16px 16px;
border-radius: 10px;
box-shadow: 2px 2px 8px rgba(0, 0, 0, 0.2);
`
